import { StyleSheet, Pressable, TextInput, Text, Clipboard, Linking, View } from "react-native";
import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { colors } from "../Global/Colors";
import { Entypo } from "@expo/vector-icons";
import AddButton from "../Components/AddButton";
import { useSignInMutation } from "../Services/authServices";
import { useDispatch } from "react-redux";
import { setUser } from "../features/User/userSlice";

const FavoriteAdress = ({ navigation }) => {
  const { favorite } = useSelector(state => state.userReducer.value);
  const [textFavorita, setTextFavorita] = useState("");
  const dispatch = useDispatch();

  const onChangeText = (nuevoValor) => {
    setTextFavorita(nuevoValor);
  };

  const guardarDireccionFavorita = async () => {
    try {
      const [triggerSignIn, resultSignIn] = useSignInMutation();
      if (resultSignIn.isSuccess) {
        dispatch(
          setUser({
            favorite: resultSignIn.data.favorite,
          })
        );
      }

      const request = {
        favorite: textFavorita || favorite,
      };

      // Asegúrate de manejar la lógica de la mutación de manera correcta
      await triggerSignIn(request);

      alert("Dirección guardada");
      navigation.goBack();
    } catch (error) {
      console.error("Error al guardar la dirección favorita:", error);
      // Manejar el error de manera adecuada
    }
  };

  const copyTextAdress = () => {
    const texto = textFavorita || favorite;
    Clipboard.setString(texto);
    alert("Texto copiado al portapapeles");
  };

  const redirectToMap = () => {
    const texto = textFavorita || favorite;
    Linking.openURL(`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(texto)}`);
  };

    return (
        <View>
            <View style={styles.card} >
                <Text style={styles.text}>
                    Direccion favorita:
                </Text>
                <Text style={styles.text}>
                    { (favorite == undefined) ?  `${textFavorita}`  :  `${favorite}` }
                </Text>

                <View  style={styles.buttons}>
                    <Pressable style={styles.pressable} onPress={redirectToMap}>
                        <Entypo name="map" size={32} color="black">
                            <Text style={styles.text2}> Ir en MAPS</Text>
                        </Entypo>
                    </Pressable>

                    <Pressable style={styles.pressable} onPress={copyTextAdress}>
                        <Entypo name="copy" size={32} color="black">
                            <Text style={styles.text2}> Copiar txt</Text>
                        </Entypo>
                    </Pressable>
                </View>
            </View>
            <Text style={styles.raya}></Text>
            <Text style={styles.text}> Cambiar direccion ?</Text>
            <TextInput
                id='textFav'
                style={styles.input}
                onChangeText={onChangeText}
                value={textFavorita}
                placeholder="Direccion Favorita"

            />
              <AddButton onPress={guardarDireccionFavorita} title="Confirmar dirección nueva" />
            {/* <AddButton
                onPress={onConfirm}
                title="Confirmar dirección nueva"
            /> */}
        </View>
    );
};

export default FavoriteAdress;

const styles = StyleSheet.create({
    container: {
        justifyContent: 'flex-start',
        alignItems: 'center'
    }, raya: {
        borderWidth: 5,
        backgroundColor: "black",
        borderRadius: 2,
        marginBottom: 10,
        width: "100%"
    },
    text: {
        paddingVertical: 20,
        fontFamily: 'Josefin',
        fontSize: 18
    }, card: {
        height: "auto",
        backgroundColor: colors.colorblanco,
        padding: 2,
        margin: 10,
        borderWidth: 2,
        borderRadius: 10,
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
    },
    card2: {
        margin: 14,
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
    },
    text2: {
        paddingLeft: "45%",
        paddingVertical: 20,
        fontFamily: 'Josefin',
        fontSize: 18
    }, input: {
        height: 40,
        width: 220,
        margin: 4,
        borderWidth: 1,
        padding: 10,
    }, pressable: {
        margin: 2,
        padding: 5,
        borderWidth: 1,
        borderRadius: 8,
    }, buttons: {
        marginBottom: 20
    }
});
