import { StyleSheet, Pressable, TextInput, Text, View } from "react-native";
import React, { useEffect, useState } from "react";

import { colors } from "../Global/Colors";
import { Entypo } from "@expo/vector-icons";
import AddButton from "../Components/AddButton";

const FavoriteAdress = ({ navigation }) => {

    const [textFavorita, setTextFavorita] = useState(" ");
    const guardadaFavorita = "";

    const onChangeText = (nuevoValor) => {
        setTextFavorita(nuevoValor);
    };

    const onConfirm = () => {

        alert("direccion guardada");
        navigation.goBack()
    }


    const copyTextAdress = () => {
        if (textFavorita = " ") {
            Clipboard.setString(guardadaFavorita);
            alert("Texto copiado al portapapeles");
        } else {
            Clipboard.setString(textFavorita);
            alert("Texto copiado al portapapeles");
        }

    }

    const redirectToMap = () => {
        if (textFavorita = " ") {
            Linking.openURL(`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(guardadaFavorita)}`);
        } else {
            Linking.openURL(`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(textFavorita)}`);
        }
    }


    useEffect(() => {

    }, [textFavorita])

    return (
        <View>
            <View style={styles.card} >
                <Text style={styles.text}>
                    Direccion favorita:
                </Text>
                <Text style={styles.text}>
                    {textFavorita}as
                </Text>

                <View  style={styles.buttons}>
                    <Pressable style={styles.pressable} onPress={redirectToMap}>
                        <Entypo name="map" size={32} color="black">
                            <Text style={styles.text2}> Ir en MAPS</Text>
                        </Entypo>
                    </Pressable>

                    <Pressable style={styles.pressable} onPress={copyTextAdress}>
                        <Entypo name="copy" size={32} color="black">
                            <Text style={styles.text2}> Copiar txt</Text>
                        </Entypo>
                    </Pressable>
                </View>
            </View>
            <Text style={styles.raya}></Text>
            <Text style={styles.text}> Cambiar direccion ?</Text>
            <TextInput
                id='textFav'
                style={styles.input}
                onChangeText={onChangeText}
                value={textFavorita}
                placeholder="Direccion Favorita"

            />
            <AddButton
                onPress={onConfirm}
                title="Confirmar direcciÃ³n nueva"
            />
        </View>
    );
};

export default FavoriteAdress;

const styles = StyleSheet.create({
    container: {
        justifyContent: 'flex-start',
        alignItems: 'center'
    }, raya: {
        borderWidth: 5,
        backgroundColor: "black",
        borderRadius: 2,
        marginBottom: 10,
        width: "100%"
    },
    text: {
        paddingVertical: 20,
        fontFamily: 'Josefin',
        fontSize: 18
    }, card: {
        height: "auto",
        backgroundColor: colors.colorblanco,
        padding: 2,
        margin: 10,
        borderWidth: 2,
        borderRadius: 10,
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
    },
    card2: {
        margin: 14,
        flexDirection: "column",
        justifyContent: "space-between",
        alignItems: "center",
    },
    text2: {
        paddingLeft: "45%",
        paddingVertical: 20,
        fontFamily: 'Josefin',
        fontSize: 18
    }, input: {
        height: 40,
        width: 220,
        margin: 4,
        borderWidth: 1,
        padding: 10,
    }, pressable: {
        margin: 2,
        padding: 5,
        borderWidth: 1,
        borderRadius: 8,
    }, buttons: {
        margin: 10
    }
});
