import { StyleSheet, Text, View } from "react-native"; 
import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import AddButton from "../Components/AddButton";
import AddressItem from '../Components/AddressItem'
import { useGetUserLocationQuery } from "../Services/shopServices";
import { firebase } from '../firebase'; 

const ListAddress = ({ navigation }) => {
    const { location, localId } = useSelector((state) => state.userReducer.value);
    const {data:userLocationQuery} = useGetUserLocationQuery(localId) 

    const { address, inputtextoa, inputtextob, latitude, longitude } = userLocationQuery;
  const getUserLocation = async (localId) => {
            const snapshot = await firebase.database().ref(`Locations/${localId}`).once('value');
            return snapshot.val();
        
        };

    useEffect(() => {
        
        console.log("alora")
        console.log(inputtextoa )
        console.log(userLocationQuery)
    }, []);

    return location?.latitude || userLocationQuery ? (
          <AddressItem 
        location={location?.latitude ? location: userLocationQuery}
        navigation={navigation} /> 
    ) : (
        <View style = {styles.container}>
             
             <Text style={styles.text}>No location set</Text>
            <AddButton
                title="Set location"
                onPress={() => navigation.navigate("Location Selector")}
            />
        </View>
    );
  
};

export default ListAddress;

const styles = StyleSheet.create({
    container: {
        justifyContent: 'flex-start',
        alignItems: 'center'
    },
    text: {
        paddingVertical: 20,
        fontFamily: 'Josefin',
        fontSize: 18
    },
    text2: {
        paddingLeft: "45%",
        paddingVertical: 20,
        fontFamily: 'Josefin',
        fontSize: 18
    }
});
