import { Image, Text, Pressable, StyleSheet, TextInput, View } from "react-native";
import React, { useState, useEffect } from "react";
import AddButton from "../Components/AddButton"; 
import { useSelector } from "react-redux";
import { useGetProfileImageQuery } from "../Services/shopServices"; 
import { useDispatch } from "react-redux"; 
import { signOut } from "../features/User/userSlice";  
import { deleteSession } from "../SQLite";

import { colors } from "../Global/Colors";
const MyProfile = ({navigation}) => {
    // const {profileImage, imageCamera} = useSelector(state => state.authReducer.value);

    const {email, localId, profileImage} = useSelector(state => state.userReducer.value)
    const {data: image} = useGetProfileImageQuery(localId) 

    const [textFavorita, setTextFavorita] = useState(" ");

    useEffect(() => {
        console.log("aca")
        console.log(email)
        console.log("esto ID:",localId)
        console.log("imagen", image);
        console.log("aca")
        console.log("cameraImage", cameraImage)
        console.log("profileImage",profileImage)
        
    }, []);

    
    const dispatch = useDispatch()

    const cameraImage = image?.image

    const launchCamera = async () => {
        navigation.navigate('Image Selector')
    };
   
    
const onSignout = async () => {
    try{
        
        console.log("deleting session...")
        const response = await deleteSession(localId)
        dispatch(signOut())
        console.log("session deleted:")
        console.log(response)
    }
    catch(error){
        console.log("Error mientras sign out")
        console.log(error.message)
    }
}

    const launchLocation = async () => {
        navigation.navigate('List Address')
    }
    const favoritelocation = async () => {
        navigation.navigate('Favorite Address')
    }




    console.log(profileImage);

    return (
       
        <View style={styles.container}>
            <Text> Usuario {email} </Text>
            {profileImage || cameraImage  ? (
                <Image
                    source={{uri: profileImage || cameraImage}}
                    style={styles.image}
                    resizeMode="cover"
                />
            ) : (
                <Image
                    source={require("../Assets/Images/defaultProfile.png")}
                    style={styles.image}
                    resizeMode="cover"
                />
            )}
            <AddButton onPress={launchCamera} title="Agregar/Cambiar foto" />
           <AddButton onPress={launchLocation} title="Lugar del auto" />  
           {/* <AddButton onPress={favoritelocation} title="Mi direccion Favorita" />   */}
           <Text>{textFavorita}</Text>
    <TextInput
            style={styles.input}
            onChangeText={setTextFavorita(textFavorita)}
            value={textFavorita}
            placeholder="comentario"
        />
           <AddButton onPress={onSignout} title="Cerrar SesiÃ³n" />  
        </View>
    );
};

export default MyProfile;

const styles = StyleSheet.create({
    container: {
        backgroundColor: colors.colorblanco,
        height: "100%",
        padding: 10,
        gap: 15,
        alignItems: "center",
        justifyContent: "flex-start",
    },
    image: {
        width: 100,
        height: 100,
        borderRadius: 50,
    },
});
