import { StyleSheet, Text, View } from "react-native";
import React, { useEffect, useState } from "react";
import * as Location from "expo-location";
import AddButton from "../Components/AddButton";
import { usePostUserLocationMutation } from "../Services/shopServices";
import { useDispatch, useSelector } from "react-redux";
import { setUserLocation } from "../features/User/userSlice";
import { colors } from "../Global/Colors";
import MapPreview from "../Components/MapPreview";
import { google_maps_api_key } from "../Database/firebaseConfig";

const LocationView = ({  navigation }) => {

    const [error, setError] = useState("");

    const [address, setAddress] = useState(null);
    const [location, setLocation] = useState(null);

    const {localId} = useSelector(state => state.userReducer.value)
    const [triggerPostAddress, result] = usePostUserLocationMutation(); 



    const goBack = () => {
        navigation.goBack()
    }
    
     fffff =() => {
        const locationFormatted = {
            latitude: -45.22223,
            longitude: 12.23232222,
            address: "acoyte 1532",
        }
        console.log(locationFormatted)
    }


    useEffect(() => {
        setAddress("acoyte 1432")
        setLocation({ latitude: -45.22223,
            longitude: 12.23232222})
    
    }, []);
 

    //Reverse geocoding
    useEffect(() => {
        (async () => {
            try {
                if (location.latitude) {
                    const url_reverse_geocode = `https://maps.googleapis.com/maps/api/geocode/json?latlng=${location.latitude},${location.longitude}&key=${google_maps_api_key}`;
                    const response = await fetch(url_reverse_geocode);
                    const data = await response.json();
                    setAddress(data.results[0].formatted_address);
                }
            } catch (error) {
                setError(error.message);
            }
        })();
    }, [location]);

    return (
        <View style={styles.container}>
            <Text
                style = {styles.text}
            >My Address</Text>
            {/* Flatlist con las directions */}
            {location ? (
                <>
                    <Text 
                        style = {styles.text}
                    >Lat: {location.latitude}, long: {location.longitude}.
                    </Text>
                    <MapPreview location={location} />
                    <Text style={styles.address}>
                        Adress: {address}
                    </Text>
                    <AddButton
                        onPress={goBack}
                        title="goBack"
                    />
                </>
            ) : (
                <>
                    <View style={styles.noLocationContainer}>
                        <Text>{error}</Text>
                    </View>
                </>
            )}
        </View>
    );
};

export default LocationView;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: "center",
        justifyContent: "flex-start",
    },
    text: {
        paddingTop: 20,
        fontFamily: 'Josefin',
        fontSize: 18
    },
    noLocationContainer: {
        width: 200,
        height: 200,
        borderWidth: 2,
        borderColor: colors.peach,
        padding: 10,
        justifyContent: "center",
        alignItems: "center",
    },
    address: {
        padding: 10,
        // fontFamily: "Ubuntu",
        fontSize: 16,
    },
});
